    private void btEntrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btEntrarActionPerformed
        String email = cxEmail.getText();
        String senha = new String(pfSenha.getPassword());

        int tipoUsuario = verificarUsuario(email, senha);

        switch (tipoUsuario) {
        // Leva para a pÃ¡gina de cliente
            case 0:
                FormCatalogo.getCatalogo().setVisible(true);
                break;
        // Leva para a pÃ¡gina de fornecedor
            case 1:
                
                break;
            default:
                JOptionPane.showMessageDialog(null, "Email e/ou senha incorretos", "Credenciais invÃ¡lidas", JOptionPane.ERROR_MESSAGE);
                cxEmail.setText(""); 
                pfSenha.setText("");
                cxEmail.requestFocus();
                break;
        }
    }//GEN-LAST:event_btEntrarActionPerformed

